import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, Button } from 'react-native';
import { FlatList } from 'react-native-gesture-handler';

function Anasayfa({ route, navigation }) {
	const [bilgi, setBilgi] = useState([]);

	var istek = new XMLHttpRequest();
	istek.onreadystatechange = function () {
		var jsonlar = JSON.parse(this.responseText);
		var dizi = jsonlar.hayvanlar;
		setBilgi(dizi);
	};
	istek.open('GET', '../db/db.json', true);
	istek.send();
	return (
		<View style={styles.container}>
			<Text style={styles.text}>Ku≈ü Sesleri</Text>
			<FlatList
				data={bilgi}
				keyExtractor={(item, index) => index}
				renderItem={({ item }) => {
					return (
						<View>
							<Text>{item.name}</Text>
						</View>
					);
				}}
			/>

			<Button
				title="Durdur"
				onPress={() => {
					alert('Hello');
				}}
			/>
			<Button title="Detaylar" onPress={() => navigation.navigate('Detaylar')} />
		</View>
	);
}
const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: '#ddd',
	},
	text: {
		textAlign: 'center',
		fontSize: 50,
		color: '#000',
		backgroundColor: '#ddd',
		fontFamily: 'Cochin',
		padding: 10,
	},
	img: {
		width: 110,
		height: 110,
		borderWidth: 1,
		borderColor: 'black',
		borderRadius: 30,
		margin: 5,
	},
});
export default Anasayfa;
