import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, Button } from 'react-native';
import { FlatList } from 'react-native-gesture-handler';

const URL = '../db/db.json';
function Anasayfa({ route, navigation }) {
	const [bilgi, setBilgi] = useState([]);

	useEffect(() => {
		fetch(URL)
			.then((r) => r.json())
			.then((d) => {
				const dizi = d.hayvanlar;
				console.log(dizi);
				setBilgi(dizi);
			});
	}, []);

	return (
		<View style={styles.container}>
			<Text style={styles.text}>Ku≈ü Sesleri</Text>
			<FlatList
				data={bolum}
				keyExtractor={(item, index) => index}
				renderItem={({ item }) => {
					return (
						<View>
							<Text style={styles.text}>
								{item.id} .{item.name}
							</Text>
						</View>
					);
				}}
				ListFooterComponent={() => {
					return <Text style={styles.end}>--SON--</Text>;
				}}
			/>

			<Button
				title="Durdur"
				onPress={() => {
					alert('Hello');
				}}
			/>
			<Button title="Detaylar" onPress={() => navigation.navigate('Detaylar')} />
		</View>
	);
}
const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: '#ddd',
	},
	text: {
		textAlign: 'center',
		fontSize: 50,
		color: '#000',
		backgroundColor: '#ddd',
		fontFamily: 'Cochin',
		padding: 10,
	},
	img: {
		width: 110,
		height: 110,
		borderWidth: 1,
		borderColor: 'black',
		borderRadius: 30,
		margin: 5,
	},
});
export default Anasayfa;
