import React from 'react';
import { StyleSheet, View, Text, Button, Image, TouchableOpacity } from 'react-native';
import { Audio } from 'expo-av';

function Anasayfa({ route, navigation }) {
	var sound = new Audio.Sound();
	const status = {
		shouldPlay: false,
	};
	this.sound.loadAsync(
		require('../sound/zapsplat_household_air_cooler_electric_on_run_off_short_rear_pov_inlet_002_58335.mp3'),
		status,
		false
	);

	function sesCal() {
		this.sound.playAsync();
	}
	return (
		<View style={styles.container}>
			<Text style={styles.text}>Anasayfa</Text>
			<View style={{ justifyContent: 'center', flexDirection: 'row' }}>
				<TouchableOpacity onPress={this.sesCal.bind(this)}>
					<Image style={styles.img} source={require('../image/angry-bird-black-icon.png')} />
				</TouchableOpacity>
				<Image style={styles.img} source={require('../image/angry-bird-green-icon.png')} />
				<Image style={styles.img} source={require('../image/angry-bird-icon.png')} />
			</View>
			<View style={{ justifyContent: 'center', flexDirection: 'row' }}>
				<Image style={styles.img} source={require('../image/angry-bird-white-icon.png')} />
				<Image style={styles.img} source={require('../image/angry-bird-yellow-icon.png')} />
				<Image style={styles.img} source={require('../image/Bird-blue-icon.png')} />
			</View>
			<View style={{ justifyContent: 'center', flexDirection: 'row' }}>
				<Image style={styles.img} source={require('../image/Bird-white-icon.png')} />
				<Image style={styles.img} source={require('../image/The-Extinct-Flightless-Pidgin-Bird-icon.png')} />
				<Image style={styles.img} source={require('../image/Things-Bird-icon.png')} />
			</View>

			<Button title="Detaylar" onPress={() => navigation.navigate('Detaylar')}></Button>
		</View>
	);
}
const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: '#ddd',
	},
	text: {
		textAlign: 'center',
		fontSize: 50,
		color: '#000',
		backgroundColor: '#ddd',
		fontFamily: 'Cochin',
		padding: 10,
	},
	img: {
		width: 110,
		height: 110,
		borderWidth: 1,
		borderColor: 'black',
		borderRadius: 30,
		margin: 5,
	},
});
export default Anasayfa;
