import React from 'react';
import { StyleSheet, View, Text, Button, Image } from 'react-native';
import { Audio } from 'expo-av';

function Anasayfa({ route, navigation }) {
	return (
		<View style={styles.container}>
			<Text style={styles.text}>Anasayfa</Text>
			<View style={{ justifyContent: 'center', flexDirection: 'row' }}>
				<Image
					onPress={() => alert('Hello')}
					style={styles.img}
					source={require('../image/Household-Washing-Machine-icon.png')}
				/>
				<Image style={styles.img} source={require('../image/Hair-Hair-Dryer-icon.png')} />
				<Image style={styles.img} source={require('../image/Bird-icon.png')} />
			</View>
			<View style={{ justifyContent: 'center', flexDirection: 'row' }}>
				<Image style={styles.img} source={require('../image/Household-Washing-Machine-icon.png')} />
				<Image style={styles.img} source={require('../image/Hair-Hair-Dryer-icon.png')} />
				<Image style={styles.img} source={require('../image/Bird-icon.png')} />
			</View>
			<View style={{ justifyContent: 'center', flexDirection: 'row' }}>
				<Image style={styles.img} source={require('../image/Household-Washing-Machine-icon.png')} />
				<Image style={styles.img} source={require('../image/Hair-Hair-Dryer-icon.png')} />
				<Image style={styles.img} source={require('../image/Bird-icon.png')} />
			</View>
			<View style={{ justifyContent: 'center', flexDirection: 'row' }}>
				<Image style={styles.img} source={require('../image/Household-Washing-Machine-icon.png')} />
				<Image style={styles.img} source={require('../image/Hair-Hair-Dryer-icon.png')} />
				<Image style={styles.img} source={require('../image/Bird-icon.png')} />
			</View>
			<Button title="Detaylar" onPress={() => navigation.navigate('Detaylar')}></Button>
		</View>
	);
}
const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: '#ddd',
	},
	text: {
		textAlign: 'center',
		fontSize: 50,
		color: '#000',
		backgroundColor: '#ddd',
		fontFamily: 'Cochin',
		padding: 10,
	},
	img: {
		width: 100,
		height: 100,
		borderWidth: 1,
		borderColor: 'black',
		borderRadius: 30,
		margin: 5,
	},
});
export default Anasayfa;
