import React from 'react';
import { StyleSheet, View, Text, Button } from 'react-native';

function Detaylar({ route, navigation }) {
	const { name, bilgi, ses } = route.params;
	return (
		<View style={styles.container}>
			<Text>
				{name}.{bilgi}.{ses}
			</Text>
			<Button title="Anasayfaya Git" onPress={() => navigation.navigate('Anasayfa')} />
		</View>
	);
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: '#B4EC29',
	},
	baslik: {
		textAlign: 'center',
		fontSize: 50,
		color: '#000',
		backgroundColor: '#EC297C',
		fontFamily: 'Cochin',
		padding: 10,
	},
	text: {
		textAlign: 'center',
		fontSize: 26,
		color: '#000',
		backgroundColor: '#EC297C',
		margin: 4,
		width: '50%',
		paddingTop: 36,
		marginLeft: 20,
		marginRight: 30,
		borderRadius: 30,
	},
	opcty: {
		width: '30%',
		height: 120,
		borderWidth: 1,
		borderColor: 'black',
		backgroundColor: '#EC297C',
		borderRadius: 30,
		margin: 5,
		marginLeft: 30,
	},
	img: {
		width: '100%',
		height: 120,
	},
});
export default Detaylar;
