import React from 'react';
import { StyleSheet, View, Text, Button, Image, TouchableOpacity } from 'react-native';
import { Audio } from 'expo-av';

function Anasayfa({ route, navigation }) {
	var ses = new Audio.Sound();
	ses.loadAsync(require('../sound/Bird_in_Rain-Mike_Koenig-441535833.mp3'));

	return (
		<View style={styles.container}>
			<Text style={styles.text}>Anasayfa</Text>
			<View style={{ justifyContent: 'center', flexDirection: 'row' }}>
				<TouchableOpacity
					onPress={() => {
						ses.stopAsync();
						ses.playAsync();
					}}
				>
					<Image style={styles.img} source={require('../image/angry-bird-black-icon.png')} />
				</TouchableOpacity>
				<TouchableOpacity
					onPress={() => {
						ses.stopAsync();
						ses.playAsync();
					}}
				>
					<Image style={styles.img} source={require('../image/angry-bird-green-icon.png')} />
				</TouchableOpacity>
				<TouchableOpacity
					onPress={() => {
						ses.stopAsync();
						ses.playAsync();
					}}
				>
					<Image style={styles.img} source={require('../image/angry-bird-icon.png')} />
				</TouchableOpacity>
			</View>
			<View style={{ justifyContent: 'center', flexDirection: 'row' }}>
				<TouchableOpacity
					onPress={() => {
						ses.stopAsync();
						ses.playAsync();
					}}
				>
					<Image style={styles.img} source={require('../image/angry-bird-white-icon.png')} />
				</TouchableOpacity>
				<TouchableOpacity
					onPress={() => {
						ses.stopAsync();
						ses.playAsync();
					}}
				>
					<Image style={styles.img} source={require('../image/angry-bird-yellow-icon.png')} />
				</TouchableOpacity>
				<TouchableOpacity
					onPress={() => {
						ses.stopAsync();
						ses.playAsync();
					}}
				>
					<Image style={styles.img} source={require('../image/Bird-blue-icon.png')} />
				</TouchableOpacity>
			</View>
			<View style={{ justifyContent: 'center', flexDirection: 'row' }}>
				<TouchableOpacity
					onPress={() => {
						ses.stopAsync();
						ses.playAsync();
					}}
				>
					<Image style={styles.img} source={require('../image/Bird-white-icon.png')} />
				</TouchableOpacity>
				<TouchableOpacity
					onPress={() => {
						ses.stopAsync();
						ses.playAsync();
					}}
				>
					<Image
						style={styles.img}
						source={require('../image/The-Extinct-Flightless-Pidgin-Bird-icon.png')}
					/>
				</TouchableOpacity>
				<TouchableOpacity
					onPress={() => {
						ses.stopAsync();
						ses.playAsync();
					}}
				>
					<Image style={styles.img} source={require('../image/Things-Bird-icon.png')} />
				</TouchableOpacity>
			</View>

			<Button title="Detaylar" onPress={() => navigation.navigate('Detaylar')}></Button>
		</View>
	);
}
const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: '#ddd',
	},
	text: {
		textAlign: 'center',
		fontSize: 50,
		color: '#000',
		backgroundColor: '#ddd',
		fontFamily: 'Cochin',
		padding: 10,
	},
	img: {
		width: 110,
		height: 110,
		borderWidth: 1,
		borderColor: 'black',
		borderRadius: 30,
		margin: 5,
	},
});
export default Anasayfa;
