import React from 'react';
import { StyleSheet, View, Text, Image } from 'react-native';

function Detaylar({ route }) {
	const { name, bilgi, ses, resim } = route.params;
	return (
		<View style={styles.container}>
			<Text style={styles.baslik}>{name}</Text>
			<Image resizeMode="contain" style={styles.img} source={{ uri: resim }} />
			<Text style={styles.info}>{bilgi}</Text>
		</View>
	);
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: '#B4EC29',
	},
	baslik: {
		textAlign: 'center',
		fontSize: 50,
		color: '#000',
		backgroundColor: '#EC297C',
		fontFamily: 'Cochin',
		padding: 10,
	},
	text: {
		textAlign: 'center',
		fontSize: 26,
		color: '#000',
		backgroundColor: '#EC297C',
		margin: 4,
		width: '50%',
		paddingTop: 36,
		marginLeft: 20,
		marginRight: 30,
		borderRadius: 30,
	},
	info: {
		width: '90%',
		height: 120,
		borderWidth: 1,
		borderColor: 'black',
		backgroundColor: '#EC297C',
		borderRadius: 30,
		marginTop: 30,
		textAlign: 'center',
	},
	img: {
		marginTop: 30,
		width: '100%',
		height: 120,
	},
});
export default Detaylar;
