import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Image } from 'react-native';
import { FlatList } from 'react-native-gesture-handler';
const DATA = [
	{
		id: 1,
		isim: 'Aslan',
		sesyolu: '',
		resimyolu: 'https://icons.iconarchive.com/icons/martin-berube/square-animal/72/Lion-icon.png',
		bilgi: '',
	},
	{ id: 2, isim: 'Kaplan', sesyolu: '', resimyolu: '', bilgi: '' },
	{ id: 3, isim: 'Kedi', sesyolu: '', resimyolu: '', bilgi: '' },
	{ id: 4, isim: 'Köpek', sesyolu: '', resimyolu: '', bilgi: '' },
	{ id: 5, isim: 'Ördek', sesyolu: '', resimyolu: '', bilgi: '' },
	{ id: 6, isim: 'Ayı', sesyolu: '', resimyolu: '', bilgi: '' },
	{ id: 7, isim: 'Arı', sesyolu: '', resimyolu: '', bilgi: '' },
	{ id: 8, isim: 'Fil', sesyolu: '', resimyolu: '', bilgi: '' },
	{ id: 9, isim: 'Zürafa', sesyolu: '', resimyolu: '', bilgi: '' },
	{ id: 10, isim: 'İnek', sesyolu: '', resimyolu: '', bilgi: '' },
	{ id: 11, isim: 'Koyun', sesyolu: '', resimyolu: '', bilgi: '' },
	{ id: 12, isim: 'Keçi', sesyolu: '', resimyolu: '', bilgi: '' },
];

function Anasayfa({ route, navigation }) {
	return (
		<View style={styles.container}>
			<Text style={styles.baslik}>Hayvanlar</Text>
			<FlatList
				data={DATA}
				keyExtractor={(item, index) => index.toString()}
				renderItem={({ item }) => {
					return (
						<View style={{ flex: 2 }}>
							<TouchableOpacity>
								<Image resizeMode="contain" style={styles.img} source={{ uri: item.resimyolu }} />
							</TouchableOpacity>
						</View>
					);
				}}
			/>
		</View>
	);
}
const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: '#ddd',
	},
	baslik: {
		textAlign: 'center',
		fontSize: 50,
		color: '#000',
		backgroundColor: '#ddd',
		fontFamily: 'Cochin',
		padding: 10,
	},
	text: {
		textAlign: 'left',
		fontSize: 26,
		color: '#000',
		backgroundColor: '#ddd',
		fontFamily: 'Cochin',
		borderWidth: 2,
		borderColor: 'black',
		margin: 4,
		width: '96%',
		borderRadius: 30,
		paddingLeft: 10,
	},
	img: {
		width: '96%',
		height: 240,
		borderWidth: 1,
		borderColor: 'black',
		borderRadius: 30,
		margin: 5,
	},
});
export default Anasayfa;
