import React, { useState } from 'react';
import { StyleSheet, View, Text, Button, Image, TouchableOpacity } from 'react-native';
import { Audio } from 'expo-av';

function Anasayfa({ route, navigation }) {
	const [bilgi, setBilgi] = useState([]);

	fetch('../db/db.json')
		.then((r) => r.json())
		.then((d) => {
			setBilgi(d);
		});

	var ses1 = new Audio.Sound();
	var ses2 = new Audio.Sound();
	var ses3 = new Audio.Sound();
	var ses4 = new Audio.Sound();
	var ses5 = new Audio.Sound();
	var ses6 = new Audio.Sound();
	var ses7 = new Audio.Sound();
	var ses8 = new Audio.Sound();
	var ses9 = new Audio.Sound();

	return (
		<View style={styles.container}>
			<Text style={styles.text}>Ku≈ü Sesleri</Text>
			<View style={{ justifyContent: 'center', flexDirection: 'row' }}>
				<TouchableOpacity
					onPress={() => {
						alert('Hello');
					}}
				>
					<Image style={styles.img} source={require('../image/angry-bird-black-icon.png')} />
				</TouchableOpacity>
				<TouchableOpacity
					onPress={() => {
						alert('Hello');
					}}
				>
					<Image style={styles.img} source={require('../image/angry-bird-green-icon.png')} />
				</TouchableOpacity>
				<TouchableOpacity
					onPress={() => {
						alert('Hello');
					}}
				>
					<Image style={styles.img} source={require('../image/angry-bird-icon.png')} />
				</TouchableOpacity>
			</View>
			<View style={{ justifyContent: 'center', flexDirection: 'row' }}>
				<TouchableOpacity
					onPress={() => {
						alert('Hello');
					}}
				>
					<Image style={styles.img} source={require('../image/angry-bird-white-icon.png')} />
				</TouchableOpacity>
				<TouchableOpacity
					onPress={() => {
						alert('Hello');
					}}
				>
					<Image style={styles.img} source={require('../image/angry-bird-yellow-icon.png')} />
				</TouchableOpacity>
				<TouchableOpacity
					onPress={() => {
						alert('Hello');
					}}
				>
					<Image style={styles.img} source={require('../image/Bird-blue-icon.png')} />
				</TouchableOpacity>
			</View>
			<View style={{ justifyContent: 'center', flexDirection: 'row' }}>
				<TouchableOpacity
					onPress={() => {
						alert('Hello');
					}}
				>
					<Image style={styles.img} source={require('../image/Bird-white-icon.png')} />
				</TouchableOpacity>
				<TouchableOpacity
					onPress={() => {
						alert('Hello');
					}}
				>
					<Image
						style={styles.img}
						source={require('../image/The-Extinct-Flightless-Pidgin-Bird-icon.png')}
					/>
				</TouchableOpacity>
				<TouchableOpacity
					onPress={() => {
						alert('Hello');
					}}
				>
					<Image style={styles.img} source={require('../image/Things-Bird-icon.png')} />
				</TouchableOpacity>
			</View>
			<View style={{ justifyContent: 'center', flexDirection: 'row' }}>
				<TouchableOpacity
					onPress={() => {
						alert('Hello');
					}}
				>
					<Image style={styles.img} source={require('../image/Bird-white-icon.png')} />
				</TouchableOpacity>
				<TouchableOpacity
					onPress={() => {
						alert('Hello');
					}}
				>
					<Image
						style={styles.img}
						source={require('../image/The-Extinct-Flightless-Pidgin-Bird-icon.png')}
					/>
				</TouchableOpacity>
				<TouchableOpacity
					onPress={() => {
						alert('Hello');
					}}
				>
					<Image style={styles.img} source={require('../image/Things-Bird-icon.png')} />
				</TouchableOpacity>
			</View>
			<View style={{ flexDirection: 'row', justifyContent: 'space-evenly', alignItems: 'stretch' }}>
				<Button
					title="Durdur"
					onPress={() => {
						alert('Hello');
					}}
				/>
				<Button title="Detaylar" onPress={() => navigation.navigate('Detaylar')} />
			</View>
		</View>
	);
}
const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: '#ddd',
	},
	text: {
		textAlign: 'center',
		fontSize: 50,
		color: '#000',
		backgroundColor: '#ddd',
		fontFamily: 'Cochin',
		padding: 10,
	},
	img: {
		width: 110,
		height: 110,
		borderWidth: 1,
		borderColor: 'black',
		borderRadius: 30,
		margin: 5,
	},
});
export default Anasayfa;
